// header sticty function
$(window).scroll(setScrollHeader);

$(window).on('load', setScrollHeader)

function setScrollHeader() {
    if ($(this).scrollTop() > 1){
        $('header').addClass("sticky");
    }
    else{
        $('header').removeClass("sticky");
    }
}
